var MongoClient = require('mongodb').MongoClient;
var url = "mongodb://localhost:27017/";

function mongoConnection(){
	MongoClient.connect(url, function(err, db) {
  		if (err) throw err;
  		var dbo = db.db("TPA");
  		dbo.collection("tiempoPorHora").findOne({}, function(err, result) {
    		if (err) throw err;
    		return result.desc;
  		});
	}); 

	document.getElementById("mainContent").innerHTML = mongoConnection();
}

